import { useState, useEffect } from "react"

const useStats = url => {
  const [stats, setStats] = useState()
  useEffect(() => {
    async function fetchData() {
      console.log("fetching data")
      try {
        const data = await fetch(url).then(res => res.json())
      } catch (error) {
        console.log(error)
      }

      setStats(data)
    }
    fetchData()
  }, [])
  return stats
}

export default useStats
