import { useState, useEffect } from "react";

const useStats = url => {
  const {
    0: stats,
    1: setStats
  } = useState();
  const {
    0: loading,
    1: setLoading
  } = useState(false);
  const {
    0: error,
    1: setError
  } = useState();
  useEffect(() => {
    console.log("mounting or fetching");

    async function fetchData() {
      console.log("fetching data");
      setLoading(true);
      setError();
      const data = await fetch(url).then(res => res.json()).catch(err => setError(err));
      setStats(data);
      setLoading(false);
    }

    fetchData();
  }, [url]);
  return {
    stats,
    loading,
    error
  };
};

export default useStats;
