import { useState, useEffect } from "react"

const useStats = url => {
  const [stats, setStats] = useState()
  useEffect(() => {
    function fetchData() {
      console.log("fetching data")
      const data = fetch(url).then(res => res.json())
      setStats(data)
    }
    fetchData()
  }, [])
  return stats
}

export default useStats
